
var url="http://localhost:8080/api";

var verificacion={
    id :0,
	nombre:"333",
	cedula:"333", 
	correo:"333",
	telefono:"333", 
	nombreEmpresa:"333", 
	telefonoEmpresa:"333",
	direccionEmpresa:"333"
};


function enviar(){
	verificacion.nombre=document.getElementById("nombre").value;
	verificacion.cedula=document.getElementById("cedula").value;
	verificacion.correo=document.getElementById("correo").value;  
	verificacion.telefono=document.getElementById("telefono").value;
	verificacion.nombreEmpresa=document.getElementById("nomEmpresa").value;  
	verificacion.telefonoEmpresa=document.getElementById("telEmpresa").value;  
	verificacion.direccionEmpresa=document.getElementById("dirEmpresa").value;
	  
	//document.location="../RegistroDirectorio/confirmation.html";
	 
	console.log(JSON.stringify(verificacion));
    const request = new Request(url+'/verificacion',
            {method: 'POST', 
                headers: { 'Content-Type': 'application/json'},
                body: JSON.stringify(verificacion)});
    (async ()=>{
        try{
            const response = await fetch(request);
            if (!response.ok) {console.log("error");return;}
            else{
				document.location="../RegistroDirectorio/confirmation.html";
            }
        }
        catch(e){
            
        }        
    })();    
  } 





function loaded(){
	document.getElementById("enviar").addEventListener("click",enviar);
}  


document.addEventListener("DOMContentLoaded",loaded); 













